import React, { Component } from "react";
import { Link } from "react-router-dom";

const tree1 = "Products";
const tree2 = ["Drinks", "Meal", "FruitsVegetables", "Meat", "Fish", "Bread"];
var tree = [];
var done = null;

class Breadcrumb extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: null,
      parent: null,
      data: null,
      tree: null,
    };
  }

  componentWillMount() {
    if (this.props) {
      const { title, parent, data } = this.props;
      if (data !== this.state.data) {
        this.setState(() => {
          return {
            title,
            parent,
            data,
          };
        });
        if (data) {
          this.build(title, data);
        }
      }
    }
  }

  componentDidUpdate() {
    if (this.props) {
      const { title, parent, data } = this.props;
      if (data !== this.state.data) {
        this.setState(() => {
          return {
            title,
            parent,
            data,
          };
        });
        if (data) {
          this.build(title, data);
        }
      }
    }
  }

  build = (title, data) => {
    var oldTree = [];
    if (data !== this.state.data) {
      if (tree.length < 1) {
        const categoryList = this.props.data.state.data2.categoryTree;
        tree = [];
        var itemCategories = [];

        var item = categoryList.find((c) => c.name === title);
        console.log(item);
        tree.push(item);
        this.hasParent(item, categoryList);

        var length = tree.length;
        console.log(tree);
        console.log(length);
        var l = length - 1;
        console.log(tree[l]);
        if (!done) {
          this.hasParent(tree[l], categoryList);
        }

        length = tree.length;
        console.log(tree);
        console.log(length);
        var l = length - 1;
        console.log(tree[l]);
        console.log(tree);

        if (!done) {
          this.hasParent(tree[l], categoryList);
        }
        length = tree.length;
        console.log(tree);
        console.log(length);
        var l = length - 1;
        console.log(tree[l]);
        console.log(tree);

        if (!done) {
          this.hasParent(tree[l], categoryList);
        }
      }

      if (done) {
        tree = tree.reverse();
        console.log(tree);
        this.setState(() => {
          return { tree };
        });
      }
    }
  };

  hasParent(category, categoryList) {
    console.log(category);
    if (category.id) {
      var parent = categoryList.find((c) => c.id === category.parent);
      console.log(parent);

      if (parent.id) {
        tree.push(parent);
      }

      if (parent.id === 19) {
        done = "true";
      }

      console.log(tree);
    }
  }

  newTo = (key) => {
    return {
      pathname: `${process.env.PUBLIC_URL}/no-sidebar/collection/`,
      category: key,
    };
  };

  itemLink = (product) => {
    return (
      <Link to={`${process.env.PUBLIC_URL}/left-sidebar/product/${product.id}`}>
        <h2>
          {product.name} {" / "}{" "}
        </h2>
      </Link>
    );
  };

  collectionLink = (name) => {
    return (
      <Link to={this.newTo(name)}>
        <h2>
          {name} {" / "}
        </h2>
      </Link>
    );
  };

  Title = () => {
    if (this.state.tree.length > 2 && this.props.state) {
      const products = this.props.state.data.products;

      {
        return tree.map((c) => {
          var product = products.find((e) => e.name === c.name);
          console.log(product);
          if (product) {
            console.log(product);
            return this.itemLink(product);
          } else {
            console.log(c);

            return this.collectionLink(c.name);
          }
        });
      }
    } else return null;
  };

  render() {
    const { title, parent, data } = this.props;
    //console.log(this.props);
    const tree2 = [
      "Drinks",
      "Meal",
      "FruitsVegetables",
      "Meat",
      "Fish",
      "Bread",
    ];

    // {tree1} + " / " + {tree2} + " / " + {treeN} + " / " + {treeN+1}

    //I have the category  of the item
    var treeN,
      treeN1,
      treeN2 = null;

    return (
      <div className="breadcrumb-section">
        <div className="container">
          <div className="row">
            <div className="col-md-6">
              <div className="page-title" />
            </div>
            <div className="col-md-6">
              <nav aria-label="breadcrumb" className="theme-breadcrumb">
                <ol className="breadcrumb">
                  <li className="breadcrumb-item">
                    <Link to={`${process.env.PUBLIC_URL}`}>Home</Link>
                  </li>
                  {this.state.tree ? (
                    <li className="breadcrumb-item" aria-current="page">
                      <this.Title />
                    </li>
                  ) : (
                    ""
                  )}
                  <li className="breadcrumb-item active" aria-current="page">
                    {title}
                  </li>
                </ol>
              </nav>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Breadcrumb;
