import axios from "axios";

export default function contact(email, firstname, lastname) {
  const request = axios.post("https://waldenberginc.com/mailchimp/", {
    params: { email: email, firstname: firstname, lastname: lastname },
  });
  return {
    type: CONTACT,
    payload: request,
  };
}
