import React, {Component} from "react";
import {Link} from "react-router-dom";
import {withTranslate} from "react-redux-multilingual";
import {connect} from "react-redux";

import {getCategories} from "../../../../services/index";
import {
  addToCart,
  addToWishlist,
  addToCompare,
} from "../../../../actions/indexO";

class NavBar extends Component {
  constructor(props) {
    super(props);

    this.state = {
      navClose: {right: "0px"},
      tree: null,
      addresses: null,
    };
  }

  componentWillMount() {
    console.log(this.props);
    if (window.innerWidth < 750) {
      this.setState({navClose: {right: "-410px"}});
    }
    if (window.innerWidth < 1199) {
      this.setState({navClose: {right: "-300px"}});
    }
  }
  /* meat:
          chicken*/

  componentWillReceiveProps() {
    console.log(this.props);
    if (this.props.data2) {
      this.setState(() => {
        return {
          tree: this.props.data2.categoryTree[0],
          addresses: this.props.data2.categoryTree[1],
        };
      });
    }
  }

  openNav() {
    console.log("open");
    this.setState({navClose: {right: "0px"}});
  }
  closeNav() {
    this.setState({navClose: {right: "-410px"}});
  }

  onMouseEnterHandler() {
    if (window.innerWidth > 1199) {
      document.querySelector("#main-menu").classList.add("hover-unset");
    }
  }

  handleSubmenu = (event) => {
    if (event.target.classList.contains("sub-arrow")) return;

    if (event.target.nextElementSibling.classList.contains("opensubmenu"))
      event.target.nextElementSibling.classList.remove("opensubmenu");
    else {
      document.querySelectorAll(".nav-submenu").forEach(function(value) {
        value.classList.remove("opensubmenu");
      });
      document
        .querySelector(".mega-menu-container")
        .classList.remove("opensubmenu");
      event.target.nextElementSibling.classList.add("opensubmenu");
    }
  };

  handleMegaSubmenu = (event) => {
    if (event.target.classList.contains("sub-arrow")) return;

    if (
      event.target.parentNode.nextElementSibling.classList.contains(
        "opensubmegamenu"
      )
    )
      event.target.parentNode.nextElementSibling.classList.remove(
        "opensubmegamenu"
      );
    else {
      document.querySelectorAll(".menu-content").forEach(function(value) {
        value.classList.remove("opensubmegamenu");
      });
      event.target.parentNode.nextElementSibling.classList.add(
        "opensubmegamenu"
      );
    }
  };

  items = (category) => {
    if (this.state.tree) {
      const tree = this.state.tree;
      const addresses = this.state.addresses;
      console.log(tree);
      console.log(addresses);
      console.log(category);
    }
    return (
      <div>
        <ul className="nav-submenu">
          <li>
            <Link to={`${process.env.PUBLIC_URL}/fashion`}>
              {translate("fashion")}
            </Link>
          </li>
        </ul>
      </div>
    );
  };

  render() {
    const {translate, data2} = this.props;
    return (
      <div>
        <div className="main-navbar">
          <div id="mainnav">
            <div className="toggle-nav" onClick={this.openNav.bind(this)}>
              <i className="fa fa-bars sidebar-bar"></i>
            </div>
            <ul className="nav-menu" style={this.state.navClose}>
              <li className="back-btn" onClick={this.closeNav.bind(this)}>
                <div className="mobile-back text-right">
                  <span>Back</span>
                  <i className="fa fa-angle-right pl-2" aria-hidden="true"></i>
                </div>
              </li>
              <li>
                <Link
                  to="#"
                  className="nav-link"
                  onClick={(e) => this.handleSubmenu(e)}
                >
                  {translate("meat")}
                  <span className="sub-arrow"></span>
                </Link>
              </li>
              <li>
                <Link
                  to="#"
                  className="nav-link"
                  onClick={(e) => this.handleSubmenu(e)}
                >
                  {translate("fish")}
                  <span className="sub-arrow"></span>
                </Link>
                <ul className="nav-submenu">
                  <li>
                    <Link
                      to={`${process.env.PUBLIC_URL}/left-sidebar/collection`}
                    >
                      {translate("category_left_sidebar")}
                    </Link>
                  </li>
                </ul>
              </li>
              <li>
                <Link
                  to="#"
                  className="nav-link"
                  onClick={(e) => this.handleSubmenu(e)}
                >
                  {translate("bakery")}
                  <span className="sub-arrow"></span>
                </Link>
                <ul className="nav-submenu">
                  <li>
                    <Link
                      to={`${process.env.PUBLIC_URL}/left-sidebar/product/1`}
                    >
                      {translate("left_sidebar")}
                    </Link>
                  </li>
                </ul>
              </li>
              <li className="mega-menu">
                <Link
                  to="#"
                  className="dropdown"
                  onClick={(e) => this.handleSubmenu(e)}
                >
                  {translate("dishes")}
                  <span className="sub-arrow"></span>
                </Link>
                <div className="mega-menu-container">
                  <div className="container">
                    <div className="row">
                      <div className="col mega-box">
                        <div className="link-section">
                          <div className="menu-title">
                            <h5 onClick={(e) => this.handleMegaSubmenu(e)}>
                              {translate("portfolio")}
                              <span className="sub-arrow"></span>
