import React, { Component } from "react";
import { Link } from "react-router-dom";

const tree1 = "Products";
const tree2 = ["Drinks", "Meal", "FruitsVegetables", "Meat", "Fish", "Bread"];
var tree = [];
var done = null;

class Breadcrumb extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: null,
      parent: null,
      data: null,
      tree: null,
      newTree: null,
    };
  }

  componentWillMount() {
    if (this.props) {
      const { title, parent, data } = this.props;
      if (data !== this.state.data || title !== this.state.title) {
        this.setState(() => {
          return {
            title,
            parent,
            data,
          };
        });
        if (data) {
          this.build(title, data);
        }
      }
    }
  }

  componentDidUpdate() {
    if (this.props) {
      const { title, parent, data } = this.props;
      if (data !== this.state.data || title !== this.state.title) {
        this.setState(() => {
          return {
            title,
            parent,
            data,
          };
        });
        if (data) {
          this.build(title, data);
        }
      }
    }
  }

  build = (title, data) => {
    var oldTree = [];
    oldTree = this.state.tree;
    if (data !== this.state.data) {
      if (true) {
        const categoryList = this.props.data.state.data2.categoryTree;
        tree = [];
        var item = categoryList.find((c) => c.name === title);
        tree.push(item);
        this.hasParent(item, categoryList);
        var length = tree.length;

        var l = length - 1;
        if (!done) {
          this.hasParent(tree[l], categoryList);
        }

        length = tree.length;
        var l = length - 1;

        if (!done) {
          this.hasParent(tree[l], categoryList);
        }

        length = tree.length;
        var l = length - 1;

        if (!done) {
          this.hasParent(tree[l], categoryList);
        }
      }

      if (done) {
        done = null;
        //tree = tree.reverse();
        console.log(tree);
        if (oldTree !== tree) {
          this.setState(() => {
            var treeReversed = tree.reversed();
            return { treeReversed, newTree: "true" };
          });
        }
      }
    }
  };

  hasParent(category, categoryList) {
    if (category !== undefined) {
      if (category.id) {
        var parent = categoryList.find((c) => c.id === category.parent);

        if (parent.id) {
          tree.push(parent);
        }

        if (parent.id === 19) {
          done = "true";
        }
      }
    }
  }

  newTo = (key) => {
    return {
      pathname: `${process.env.PUBLIC_URL}/no-sidebar/collection/`,
      category: key,
    };
  };

  itemLink = (product) => {
    return (
      <Link to={`${process.env.PUBLIC_URL}/left-sidebar/product/${product.id}`}>
        <h2>
          {product.name} {" / "}{" "}
        </h2>
      </Link>
    );
  };

  collectionLink = (name, i) => {
    console.log(name);

    return (
      <Link key={i} to={this.newTo(name)}>
        {name} {" / "}
      </Link>
    );
  };

  Title = () => {
    if (this.state.tree.length > 1) {
      const products = this.state.products;
      // var theTree = this.state.tree.reverse();
      {
        return this.state.tree.map((c, i) => {
          console.log(c);
          return this.collectionLink(c.name, i);
        });
      }
    } else return null;
  };

  render() {
    if (this.state.tree) {
      return (
        <div className="breadcrumb-section">
          <div className="container">
            <div className="row">
              <div className="col-md-6">
                <div className="page-title" />
              </div>
              <div className="col-md-6">
                <nav aria-label="breadcrumb" className="theme-breadcrumb">
                  <ol className="breadcrumb">
                    <li className="breadcrumb-item">
                      <Link to={`${process.env.PUBLIC_URL}`}>Home</Link>
                    </li>
                    {this.state.tree.map((c, i) => {
                      return (
                        <li key={i} className="breadcrumb-item">
                          {this.collectionLink(c.name, i)}
                        </li>
                      );
                    })}
                  </ol>
                </nav>
              </div>
            </div>
          </div>
        </div>
      );
    } else return <h1>Loading ....</h1>;
  }
}

export default Breadcrumb;
