const TIMEOUT = 9000;
var __products = null;

var pos = 1;
var isTreeLoaded = false;

var tree = {
  id: 19,
  name: "products",
  parent: "none",
  children: [],
};

var addresses = [
  {
    id: 19,
    level: 0,
    position: 0,
    name: "Product Categories",
  },
];

const getProducts = () => {
  return WooCommerce.getAsync(`products?per_page=30`)
    .then((res) => res.toJSON().body)
    .then((json) => {
      var data = json;
      Object.json1 = JSON.parse(data);
      var items = Object.json1;
      var products = [];
      items.map(
        ({
          id,
          name,
          images,
          price,
          categories,
          description,
          stock_quantity,
          short_description,
          sale_price,
          on_sale,
          tags,
        }) => {
          var img;
          var newImages = [];
          images.map((data) => {
            img = data.src;
            newImages.push(img);
          });

          const newItem = {
            categories: categories,
            category: "vegetable",
            description: description,
            discount: "10",
            id: id,
            name: name,
            new: false,
            pictures: newImages,
            length: images.length,
            price: price,
            sale: on_sale,
            salePrice: sale_price,
            shortDetails: short_description,
            stock: stock_quantity,
            brand: tags,
            colors: ["yellow", "gray", "green"],
            size: ["M", "L", "XL"],
            tags: ["nike", "caprese"],
            rating: 4,
            variants: null,
          };

          products.push(newItem); // Push the object
        }
      );
      return products;
    })
    .catch((error) => {
      return error;
    });
};

const getCategories = (parent, level) => {
  return WooCommerce.getAsync(
    `products/categories?hide_empty=false&per_page=100${parent}`
  ).then((res) => {
    if (res) {
      var data = res.toJSON().body;
      Object.json1 = JSON.parse(data);
      var items = Object.json1;
      console.log(items);

      var categories = [];

      if (items[0]) {
        if (items[0].id != undefined) {
          pos = 0;

          items.map(({id, name, parent}) => {
            const newCategory = {
              id: id,
              name: name,
              parent: parent,
              children: [],
            };
            const addr = {
              id: id,
              level: level,
              position: pos,
              name: name,
            };
            addresses.push(addr);
            categories.push(newCategory); // Push the object

            pos++;
          });
          return categories;
        }
      }
    }
  });
};

const getCategoryTree = async () => {
  var root = `&parent=19`;
  const Str = `&parent=`;
  var level = 1;

  if (isTreeLoaded === false) {
    await getCategories(root, level)
      .then((res) => {
        tree.children[0] = res;
        if (res) {
          level++;
          var i = 0;

          res.map(async (m) => {
            // With m.id I request and what I get goes into
            // m.children
            if (m) {
              if (m.id) {
                root = Str + `${m.id}`;
                console.log(root, level);
                await getCategories(root, level).then((res) => {
                  console.log(res);
                  if (res) {
                    if (tree.children) {
                      if (tree.children[0]) {
                        if (tree.children[0][i]) {
                          if (tree.children[0][i].children) {
                            var arrayPos = 0;
                            addresses.map((addr) => {
                              if (addr.id === m.id) {
                                arrayPos = addr.position;
                              }
                            });

                            if (!tree.children[0][arrayPos].children[0]) {
                              tree.children[0][arrayPos].children.push(res);
                            }

                            i++;
                          }
                        }
                      }
                    }
                    let extraLevel = level++;
                    var j = 0;
                    res.map(async (m) => {
                      if (m) {
                        console.log(m);
                        if (m.id) root = Str + `${m.id}`;
                        console.log(root, extraLevel);
                        await getCategories(root, extraLevel).then((res) => {
                          if (res) {
                            console.log(res);
                            if (tree.children) {
                              if (tree.children[0]) {
                                if (tree.children[0][i]) {
                                  if (tree.children[0][i].children) {
                                    var arrayPos = 0;
                                    addresses.map((addr) => {
                                      if (addr.id === m.id) {
                                        arrayPos = addr.position;
                                      }
                                    });

                                    if (
                                      !tree.children[0][arrayPos].children[0]
                                    ) {
                                      tree.children[0][arrayPos].children.push(
                                        res
                                      );
                                    }

                                    i++;
                                  }
                                }
                              }
                            }
                          }
                        });
                      }
                    });
                  }
                });
              }
            }
          });
        }
        isTreeLoaded = true;
        tree = tree;
        console.log(tree);
        return tree;
      })
      .catch((error) => {
        return error;
      });
  }
};

export default {
  getProducts,
  getCategoryTree,
  buyProducts: (payload, cb, timeout) =>
    setTimeout(() => cb(), timeout || TIMEOUT),
};
