import React, {Component} from "react";
import {connect} from "react-redux";
import {Link} from "react-router-dom";
import InfiniteScroll from "react-infinite-scroll-component";

import {getTotal, getCartProducts} from "../../reducers/index";
import {addToCart, addToWishlist, addToCompare} from "../../actions/indexO";
import {getVisibleproducts} from "../../services/index";
import ProductListItem from "./common/product-list-item";
import Breadcrumb from "../common/breadcrumb";

class CollectionMetro extends Component {
  constructor(props) {
    super(props);

    this.state = {
      limit: 5,
      hasMoreItems: true,
    };
  }

  componentWillMount() {
    this.fetchMoreItems();
  }

  fetchMoreItems = () => {
    if (this.state.limit >= this.props.products.length) {
      this.setState({hasMoreItems: false});
      return;
    }
    // a fake async api call
    setTimeout(() => {
      this.setState({
        limit: this.state.limit + 5,
      });
    }, 3000);
  };

  render() {
    const {
      products,
      addToCart,
      symbol,
      addToWishlist,
      addToCompare,
    } = this.props;

    return (
      <div>
        <Breadcrumb title={"Collection"} />

        <section className="section-b-space">
          <div className="collection-wrapper">
            <div className="container">
              <div className="row">
                <div className="collection-content col">
                  <div className="page-main-content">
                    <div className="top-banner-wrapper">
                      <a href="#">
                        <img
                          src="/assets/images/mega-menu/2.jpg"
                          className="img-fluid blur-up lazyload"
                          alt=""
                        />
                      </a>
                      <div className="top-banner-content small-section pb-0">
                        <h4>fashion</h4>
                        <h5>
                          Lorem Ipsum is simply dummy text of the printing and
                          typesetting industry.
                        </h5>
                        <p>
                          Lorem Ipsum is simply dummy text of the printing and
                          typesetting industry. Lorem Ipsum has been the
                          industry's standard dummy text ever since the 1500s,
                          when an unknown printer took a galley of type and
                          scrambled it to make a type specimen book. It has
                          survived not only five centuries, but also the leap
                          into electronic typesetting, remaining essentially
                          unchanged. It was popularised in the 1960s with the
                          release of Letraset sheets containing Lorem Ipsum
                          passages, and more recently with desktop publishing
                          software like Aldus PageMaker including versions of
                          Lorem Ipsum.
                        </p>
                      </div>
                    </div>
                    <div className="collection-product-wrapper">
                      <div className="section-t-space portfolio-section portfolio-padding metro-section port-col">
                        {products.length > 0 ? (
                          <InfiniteScroll
                            dataLength={this.state.limit} //This is important field to render the next data
                            next={this.fetchMoreItems}
                            hasMore={this.state.hasMoreItems}
                            loader={<div className="loading-cls"></div>}
                            endMessage={
                              <p className="seen-cls seen-it-cls">
                                <b>Yay! You have seen it all</b>
                              </p>
                            }
                          >
                            <div className="isotopeContainer row">
                              {products
                                .slice(0, this.state.limit)
                                .map((product, index) => (
                                  <div
                                    className="col-xl-3 col-sm-6 isotopeSelector"
                                    key={index}
                                  >
                                    <ProductListItem
                                      product={product}
                                      symbol={symbol}
                                      onAddToCompareClicked={() =>
                                        addToCompare(product)
                                      }
                                      onAddToWishlistClicked={() =>
                                        addToWishlist(product)
                                      }
                                      onAddToCartClicked={addToCart}
                                      key={index}
                                    />
                                  </div>
                                ))}
                            </div>
                          </InfiniteScroll>
                        ) : (
                          <div className="row">
                            <div className="col-sm-12 text-center section-b-space mt-5 no-found">
                              <img
                                src={`${process.env.PUBLIC_URL}/assets/images/empty-search.jpg`}
                                class="img-fluid mb-4"
                              />
                              <h3>
                                Sorry! Couldn't find the product you were
                                looking For!!!{" "}
                              </h3>
                              <p>
                                Please check if you have misspelt something or
                                try searching with other words.
                              </p>
                              <Link
                                to={`${process.env.PUBLIC_URL}/`}
                                class="btn btn-solid"
                              >
                                continue shopping
                              </Link>
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  products: getVisibleproducts(state.data, state.filters),
  symbol: state.data.symbol,
});

export default connect(mapStateToProps, {
  addToCart,
  addToWishlist,
  addToCompare,
})(CollectionMetro);
