import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import SimpleReactValidator from "simple-react-validator";
import { Helmet } from "react-helmet";

import { login } from "../../actions/indexO";
import Breadcrumb from "../breadcrumb";
import { withTranslate } from "react-redux-multilingual";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: "",
    };
    this.validator = new SimpleReactValidator();
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidUpdate() {
    if (this.props.state.user.log.username) {
      // alert(this.state.username + " you are currently logged in !");
      this.props.history.push("/");
    }
  }

  handleChange = (e) => {
    const n = e.currentTarget.value;
    const name = e.currentTarget.name;
    if (name === "password") {
      this.setState(() => {
        return { password: n };
      });
    } else {
      this.setState(() => {
        return { username: n };
      });
    }
  };

  handleSubmit(event) {
    event.preventDefault();
    if (this.state.username.length > 0 && this.state.password > 0) {
      const userData = {
        username: this.state.username,
        password: this.state.password,
      };
      this.props.login(userData);
    }
  }

  render() {
    const { translate } = this.props;
    return (
      <div>
        {/*SEO Support*/}
        <Helmet>
          <title>
            {translate("title_web")}| {item.category} | {item.name}
          </title>
          <meta name="description" content="Del Prado Alimentacion" />
        </Helmet>
        {/*SEO Support End */}
        <Breadcrumb title={"Login"} />

        {/*Login section*/}
        <section className="login-page section-b-space">
          <div className="container">
            <div className="top-banner-wrapper2">
              <button className="invisible-button">
                <img
                  src={`${
                    process.env.PUBLIC_URL
                  }/assets/images/mega-menu/2.jpeg`}
                  className="img-fluid"
                  alt=""
                />
              </button>
            </div>
            <div className="row">
              <div className="col-lg-6">
                <h3> {translate("login")}</h3>
                <div className="theme-card">
                  <form className="theme-form" onSubmit={this.handleSubmit}>
                    <div className="form-group">
                      <input
                        id="fname"
                        type="text"
                        className="form-control"
                        placeholder="NickName"
                        required=""
                        name="first_name"
                        value={this.state.username}
                        onChange={this.handleChange}
                      />
                      {this.validator.message(
                        "first_name",
                        this.state.username,
                        "required|alpha"
                      )}
                    </div>
                    <div className="form-group">
                      <label htmlFor="review">{translate("password")}</label>
                      <input
                        type="password"
                        className="form-control"
                        placeholder="Enter your password"
                        autoComplete="on"
                        required=""
                        name="password"
                        value={this.state.password}
                        onChange={this.handleChange}
                      />
                      {this.validator.message(
                        "password",
                        this.state.password,
                        "required|alpha"
                      )}
                    </div>
                    <button type="submit" className="btn btn-solid">
                      {translate("login")}
                    </button>
                  </form>
                </div>
              </div>
              <div className="col-lg-6 right-login">
                <h3> {translate("new_customers")}</h3>
                <div className="theme-card authentication-right">
                  <h6 className="title-font">{translate("create_account")}</h6>
                  <p>{translate("create_account_text")}</p>

                  <Link
                    to={`${process.env.PUBLIC_URL}/pages/register`}
                    className="btn btn-solid"
                    data-lng="en"
                  >
                    {translate("create_account")}
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </section>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  state,
});

export default connect(
  mapStateToProps,
  {
    login,
  }
)(withTranslate(Login));
