import {
    POST_REVIEW_DONE,
    GET_REVIEW_DONE
} from "../constants/ActionTypes";

const initialState = {
    reviews: [],
};

const reviewsReducer = (state = initialState, action) => {
    switch (action.type) {

        case POST_REVIEW_DONE:
            console.log(action)
            return state

        case GET_REVIEW_DONE:
            console.log(action)
            console.log(state)
            return {
                reviews: action.reviews
            }

        default:
            return state;
    }
};
export default reviewsReducer;
