                open={openZ}
              />
              <NestedList
                className="card-header"
                text="Gastos de entrega"
                form={gastos}
                open={openG}
              />

              <NestedList
                className="card-header"
                text="Importe de la compra"
                form={importe}
                open={false}
              />
              <NestedList
                className="card-header"
                text="Productos agotados"
                form={agotados}
                open={false}
              />
              <NestedList
                className="card-header"
                text="Devoluciones de productos"
                form={devoluciones}
                open={false}
              />
              <NestedList
                className="card-header"
                text="Desistimiento"
                form={destinamiento}
                open={false}
              />
              <NestedList
                className="card-header"
                text="Garantía"
                form={garantia}
                open={false}
              />
              <NestedList
                className="card-header"
                text="Quejas y reclamaciones"
                form={quejas}
                open={false}
              />
              <NestedList
                className="card-header"
