import {
  FETCH_SINGLE_PRODUCT,
  CHANGE_CURRENCY,
  RECEIVE_PRODUCTS,
  SEARCH_PRODUCT,
  POST_REVIEW_DONE,
  GET_REVIEW_DONE
} from "../constants/ActionTypes";

const initialState = {
  products: [],
  symbol: "$",
  product_details: [],
  product_reviews: [],
};

const productReducer = (state = initialState, action) => {
  switch (action.type) {
    case RECEIVE_PRODUCTS:
      return {
        ...state,
        products: action.products,
      };
    case FETCH_SINGLE_PRODUCT:
      if (
        state.products.findIndex(
          (product) => product.id === action.productId
        ) !== -1
      ) {
        const singleItem = state.products.reduce((itemAcc, product) => {
          return product;
        }, []);
        return {
          ...state,
          product_details: singleItem,
        };
      }

    case SEARCH_PRODUCT:
      console.log(action.productName);
      var item = {};
      state.products.map((product) => {
        //console.log(product.name);
        var thisName = product.name.toUpperCase();
        var receivedName = action.productName.toUpperCase();
        // console.log(thisName, receivedName);
        if (thisName === receivedName) {
          console.log(product);
          item = product;
        }
      });
      if (item.name) {
        console.log(item);
        return {
          ...state,
          product_details: item,
        };
      }

    case POST_REVIEW_DONE:
      return state

    case GET_REVIEW_DONE:
      console.log(action.reviews)
      return {
        ...state, product_reviews: action.reviews
      }

    case CHANGE_CURRENCY:
      return {
        ...state,
        symbol: action.symbol,
      };
    default:
      return state;
  }
};
export default productReducer;
