import React, { Component } from "react";
import { Link } from "react-router-dom";

class Breadcrumb extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: null,
      parent: null,
      data: null,
    };
  }

  componentWillMount() {
    if (this.props) {
      const { title, parent, data } = this.props;
      if (data !== this.state.data) {
        this.setState(() => {
          return {
            title,
            parent,
            data,
          };
        });
        if (data) {
          this.build(data);
        }
      }
    }
  }

  build = (data) => {
    const categoryList = this.props.data.state.data2.categoryTree;
    const products = this.props.data.state.data.products;
    const tree1 = "Products";
    const tree2 = [
      "Drinks",
      "Meal",
      "FruitsVegetables",
      "Meat",
      "Fish",
      "Bread",
    ];

    var itemCategories = [];
    var tree = [];
    tree.push(tree1);

    products.map((p) => {
      if (p.name === data.title) {
        itemCategories = p.categories;
      }
    });

    // itemCategories = ["productos","meal","mollejas"]
    //   [ {name,id}]
    var length = itemCategories.length();

    if (length <= 3) {
      itemCategories.map((n) => {
        if (n !== title && n !== tree1) {
          tree.push(n);
        }
      });
      tree.push(title);
    } else {
    }
  };

  checkTree2 = (name) => {
    var done = null;
    tree2.map((n) => {
      if (n === name) {
        done = "Done!";
      }
    });
    return done;
  };

  findParent = (itemId) => {
    var parent = null;
    categoryList.map((c) => {
      if (c.id === itemId) {
        parent = { name: c.name, parentId: c.parent };
      }
    });
    return parent;
  };

  newTo = (key) => {
    return {
      pathname: `${process.env.PUBLIC_URL}/no-sidebar/collection/`,
      category: key,
    };
  };

  itemLink = (product) => {
    return (
      <Link to={`${process.env.PUBLIC_URL}/left-sidebar/product/${product.id}`}>
        <h2>
          {product} {" / "}{" "}
        </h2>
      </Link>
    );
  };

  collectionLink = (name) => {
    return (
      <Link to={newTo(name)}>
        <h2>
          {name} {" / "}
        </h2>
      </Link>
    );
  };

  Title = (tree) => {
    return (
      <div>
        {tree.map((name) => {
          if (
            !products.map((p) => {
              if (p.name === name) {
                return itemLink(name);
              }
            })
          ) {
            return collectionLink(name);
          }
        })}
      </div>
    );
  };

  render() {
    const { title, parent, data } = this.props;
    console.log(this.props);
    const tree1 = ["Products"];
    const tree2 = [
      "Drinks",
      "Meal",
      "FruitsVegetables",
      "Meat",
      "Fish",
      "Bread",
    ];

    // {tree1} + " / " + {tree2} + " / " + {treeN} + " / " + {treeN+1}

    //I have the category  of the item
    var treeN,
      treeN1,
      treeN2 = null;

    return (
      <div className="breadcrumb-section">
        <div className="container">
          <div className="row">
            <div className="col-md-6">
              <div className="page-title" />
            </div>
            <div className="col-md-6">
              <nav aria-label="breadcrumb" className="theme-breadcrumb">
                <ol className="breadcrumb">
                  <li className="breadcrumb-item">
                    <Link to={`${process.env.PUBLIC_URL}`}>Home</Link>
                  </li>
                  {parent ? (
                    <li className="breadcrumb-item" aria-current="page">
                      {parent}
                    </li>
                  ) : (
                    ""
                  )}
                  <li className="breadcrumb-item active" aria-current="page">
                    {title}
                  </li>
                </ol>
              </nav>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Breadcrumb;
