const wooConfig = require("./wooConfig");
const WooCommerceAPI = require("woocommerce-api");

const WooCommerce = new WooCommerceAPI({
  url: wooConfig.siteUrl,
  consumerKey: wooConfig.consumerKey,
  consumerSecret: wooConfig.consumerSecret,
  wpAPI: true,
  version: "wc/v3",
});

const TIMEOUT = 9000;

var pos = 1;
var isTreeLoaded = false;
var done = false;

var tree = {
  id: 19,
  name: "products",
  parent: "none",
  children: [],
};

var addresses = [
  {
    id: 19,
    level: 0,
    position: 0,
    name: "Product Categories",
  },
];

const removeString = (res) => {
  console.log(res);
  var newParraf = res.replace(`<p>`, " ");
  console.log(newParraf);
  newParraf = newParraf.split(`</p>`, 2);
  console.log(newParraf);
  return newParraf[0];
};

const getProducts = () => {
  return WooCommerce.getAsync(`products?per_page=30`)
    .then((res) => {
      return res.toJSON().body;
    })
    .then((json) => {
      var data = json;
      Object.json1 = JSON.parse(data);
      var items = Object.json1;
      var products = [];
      console.log(items);
      items.map(
        ({
          id,
          name,
          images,
          price,
          categories,
          description,
          stock_quantity,
          short_description,
          sale_price,
          on_sale,
          tags,
          rating_count,
        }) => {
          var img;
          var newImages = [];
          images.map((data) => {
            img = data.src;
            newImages.push(img);
          });
          var discount = null;

          var stringDescription = removeString(description);
          var stringShortDescription = removeString(short_description);

          const newItem = {
            categories: categories,
            category: "vegetable",
            description: stringDescription,
            discount: sale_price,
            id: id,
            name: name,
            new: false,
            pictures: newImages,
            length: images.length,
            price: price,
            sale: on_sale,
            salePrice: sale_price,
            shortDetails: stringShortDescription,
            stock: stock_quantity,
            brand: tags,
            colors: ["yellow", "gray", "green"],
            size: ["M", "L", "XL"],
            tags: ["nike", "caprese"],
            rating: rating_count,
            variants: null,
          };

          products.push(newItem); // Push the object
        }
      );
      return products;
    })
    .catch((error) => {
      return error;
    });
};

const getCategoryTree = async () => {
  var root = `&parent=19`;
  const Str = `&parent=`;
  var level = 1;

  return await getTheCategories(root, level).then((res) => {
    return res;
  });
};

const getTheCategories = (parent, level) => {
  return WooCommerce.getAsync(
    `products/categories?hide_empty=false&per_page=100`
  ).then((res) => {
    var data = res.toJSON().body;
    Object.json1 = JSON.parse(data);
    var items = Object.json1;
    return items;
  });
};

const postReview = (data) => {
  return WooCommerce.postAsync("products/reviews", data)
    .then((response) => {})
    .catch((error) => {
      console.log(error.response.data);
    });
};

const getReview = (id) => {
  return WooCommerce.getAsync(`products/reviews?product=${id}`)
    .then((res) => {
      var data = res.toJSON().body;
      Object.json1 = JSON.parse(data);
      var reviews = Object.json1;
      return reviews;
    })
    .catch((error) => {
      console.log(error.response.data);
    });
};

export default {
  getProducts,
  getCategoryTree,
  postReview,
  getReview,
  buyProducts: (payload, cb, timeout) =>
    setTimeout(() => cb(), timeout || TIMEOUT),
};
