import React, {Component} from "react";
import {connect} from "react-redux";
import SimpleReactValidator from "simple-react-validator";
import {signup} from "../../actions/indexO";
import Breadcrumb from "../breadcrumb";

class Register extends Component {
  constructor(props) {
    super(props);

    this.state = {
      payment: "stripe",
      username: "",
      password: "",
      first_name: "",
      last_name: "",
      phone: "",
      email: "",
      country: "",
      address_1: "",
      city: "",
      state: "",
      postcode: "",
    };

    this.validator = new SimpleReactValidator();
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidUpdate() {
    console.log(this.props);
    if (this.props.state.user.log.username) {
      alert(this.state.first_name + " you are currently logged in !");
      this.props.history.push("/");
    }
  }

  handleChange = (e) => {
    let newValue = "";
    var value = e.currentTarget.value;
    newValue = value.replace(/\s/, " ");
    const name = e.currentTarget.name;
    if (e.currentTarget.name) {
      if (name === "postcode" || name === "phone") {
        this.setState(() => {
          return {[name]: value.replace(/\D/, "")};
        });
      } else {
        this.setState(() => {
          return {[name]: value};
        });
      }
    } else return null;
  };

  handleSubmit(event) {
    event.preventDefault();
    if (this.state.first_name.length > 0 && this.state.password > 0) {
      const {
        payment,
        username,
        first_name,
        password,
        last_name,
        phone,
        email,
        country,
        address_1,
        city,
        state,
        postcode,
      } = this.state;

      const userData = {
        username: username,
        first_name: first_name,
        password: password,
        last_name: last_name,
        phone: phone,
        email: email,
        country: country,
        address_1: address_1,
        city: city,
        state: state,
        postcode: postcode,
      };
      this.props.signup(userData);
    }
  }
  render() {
    return (
      <div>
        <Breadcrumb title={"create account"} />

        {/*Regsiter section*/}
        <section className="register-page section-b-space">
          <div className="container">
            <div className="row">
              <div className="col-lg-12">
                <h3>create account</h3>
                <div className="theme-card">
                  <form className="theme-form" onSubmit={this.handleSubmit}>
                    <div className="form-row">
                      <div className="col-md-6">
                        <label htmlFor="email">User Name</label>
                        <input
                          id="uname"
                          type="text"
                          className="form-control"
                          placeholder="User Name"
                          required=""
                          name="username"
                          value={this.state.username}
                          onChange={this.handleChange}
                        />
                        {this.validator.message(
                          "username",
                          this.state.username,
                          "required|alpha"
                        )}
                      </div>
                      <div className="col-md-6">
                        <label htmlFor="review">Password</label>
                        <input
                          id="password"
                          type="password"
                          autoComplete="on"
                          className="form-control"
                          placeholder="Password"
                          required=""
                          name="password"
                          value={this.state.password}
                          onChange={this.handleChange}
                        />
                        {this.validator.message(
                          "password",
                          this.state.password,
                          "required|alpha"
                        )}
                      </div>{" "}
                    </div>

                    <div className="form-row">
                      <div className="col-md-6">
                        <label htmlFor="email">email</label>
                        <input
                          id="email"
                          type="text"
                          className="form-control"
                          placeholder="Email"
                          required=""
                          name="email"
                          value={this.state.email}
                          onChange={this.handleChange}
                        />
                        {this.validator.message(
                          "email",
                          this.state.email,
                          "required|alpha"
                        )}
                      </div>
                    </div>
                    <div className="form-row">
                      <div className="col-md-6">
                        <label htmlFor="email">First Name</label>
                        <input
                          id="fname"
                          type="text"
                          className="form-control"
                          placeholder="First Name"
                          required=""
                          name="first_name"
                          value={this.state.first_name}
                          onChange={this.handleChange}
                        />
                        {this.validator.message(
                          "first_name",
                          this.state.first_name,
                          "required|alpha"
                        )}
                      </div>
                      <div className="col-md-6">
                        <label htmlFor="review">Last Name</label>
                        <input
                          id="lname"
                          type="text"
                          className="form-control"
                          placeholder="Last Name"
                          required=""
                          name="last_name"
                          value={this.state.last_name}
                          onChange={this.handleChange}
                        />
                        {this.validator.message(
                          "last_name",
                          this.state.last_name,
                          "required|alpha"
                        )}
                      </div>
                    </div>

                    <div className="checkout-title">
                      <h3>Billing Details</h3>
                    </div>
                    <div className="form-row">
                      <div className="col-md-6">
                        <label htmlFor="country ">country </label>
                        <input
                          id="country"
                          type="text"
                          className="form-control"
                          placeholder="Country"
                          required=""
                          name="country"
