import {FETCH_USERWOO, RECEIVE_USERWOO} from "../constants/ActionTypes";

const initialState = {
  categoryTree: [],
  category: [],
};

const userWooReducer = (state = initialState, action) => {
  switch (action.type) {
    case RECEIVE_USERWOO:
      return {
        ...state,
        categoryTree: action.categoryTree,
      };
    case FETCH_USERWOO:
      if (
        state.categoryTree.findIndex((category) => {
          if (category.id !== -1) {
            const singleCategory = state.categoryTree.reduce(
              (itemAcc, categoryTree) => {
                return categoryTree;
              },
              []
            );
            return {
              ...state,
              category: singleCategory,
            };
          }
        })
      );
    default:
      return state;
  }
};
export default userWooReducer;
