import React, { Component } from "react";
import { Link } from "react-router-dom";

class Breadcrumb extends Component {
  render() {
    const { title, parent, data } = this.props;
    console.log(this.props);
    const tree1 = ["Products"];
    const tree2 = [
      "Drinks",
      "Meal",
      "FruitsVegetables",
      "Meat",
      "Fish",
      "Bread",
    ];

    // {tree1} + " / " + {tree2} + " / " + {treeN} + " / " + {treeN+1}

    //I have the category  of the item
    var treeN,
      treeN1,
      treeN2 = null;

    if (data) {
      const categoryList = data.state.data2.categoryTree;
      const products = data.state.data.products;

      const checkTree2 = (name) => {
        tree2.map((n) => {
          if (n === name) {
            return "Done!";
          } else return null;
        });
      };

      const findParent = (itemId) => {
        var parent = null;
        categoryList.map((c) => {
          if (c.id === itemId) {
            parent = { name: c.name, parentId: c.parent };
          }
        });
        return parent;
      };

      var tree = [tree1]; //Mollejas    / Onion
      var dynamicTree = [];
      var done = checkTree2(title);
      console.log(done);
      if (done) {
        tree.push(title);
      } else {
        categoryList.map((c) => {
          if (c.name === title) {
            dynamicTree.push(title);
            const treeParentId = c.parent; //finds the parentId of the product
            const parent = findParent(treeParentId);
            if (parent) {
              done = checkTree2(parent.name);
              if (done) {
                return dynamicTree;
              } else {
                var newName = parent.name;
                dynamicTree.push(newName);
                console.log(dynamicTree);

                const parent2 = findParent(parent.parentId);

                if (parent2) {
                  done = checkTree2(parent2.name);
                  if (done) {
                    return dynamicTree;
                  } else {
                    newName = parent2.name;
                    dynamicTree.push(newName);
                    console.log(dynamicTree);

                    const parent3 = findParent(parent2.parentId);
                    if (parent3) {
                      done = checkTree2(parent3.name);
                      if (done) {
                        return dynamicTree;
                      } else {
                        newName = parent3.name;
                        dynamicTree.push(newName);
                        console.log(dynamicTree);

                        const parent4 = findParent(parent3.parentId);
                        if (parent4) {
                          done = checkTree2(parent3.name);
                          if (done) {
                            return dynamicTree;
                          } else {
                            newName = parent4.name;
                            dynamicTree.push(newName);
                            console.log(dynamicTree);

                            const parent5 = findParent(parent4.parentId);
                            if (parent5) {
                              newName = parent5.name;
                              dynamicTree.push(newName);
                              console.log(dynamicTree);
                            }
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
            console.log(dynamicTree);
            dynamicTree = dynamicTree.reverse();
          }
          console.log(dynamicTree);
          tree.push(dynamicTree);
          console.log(tree);
        });
      }

      console.log(categoryList);

      const newTo = (key) => {
        return {
          pathname: `${process.env.PUBLIC_URL}/no-sidebar/collection/`,
          category: key,
        };
      };

      const itemLink = (product) => {
        return (
          <Link
            to={`${process.env.PUBLIC_URL}/left-sidebar/product/${product.id}`}
          >
            <h2>
              {product} {" / "}{" "}
            </h2>
          </Link>
        );
      };

      const collectionLink = (name) => {
        return (
          <Link to={newTo(name)}>
            <h2>
              {name} {" / "}
            </h2>
          </Link>
        );
      };

      const Title = (tree) => {
        return (
          <div>
            {tree.map((name) => {
              if (
                !products.map((p) => {
                  if (p.name === name) {
                    return itemLink(name);
                  }
                })
              ) {
                return collectionLink(name);
              }
            })}
          </div>
        );
      };
    }

    return (
      <div className="breadcrumb-section">
        <div className="container">
          <div className="row">
            <div className="col-md-6">
              <div className="page-title">
                <Title />
              </div>
            </div>
            <div className="col-md-6">
              <nav aria-label="breadcrumb" className="theme-breadcrumb">
                <ol className="breadcrumb">
                  <li className="breadcrumb-item">
                    <Link to={`${process.env.PUBLIC_URL}`}>Home</Link>
                  </li>
                  {parent ? (
                    <li className="breadcrumb-item" aria-current="page">
                      {parent}
                    </li>
                  ) : (
                    ""
                  )}
                  <li className="breadcrumb-item active" aria-current="page">
                    {title}
                  </li>
                </ol>
              </nav>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Breadcrumb;
