import React, { Component } from "react";
import Slider from "react-slick";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { withTranslate } from "react-redux-multilingual";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

class NewProduct extends Component {
  constructor() {
    super();
    this.state = {
      open: false,
      items: null,
    };
  }
  componentDidUpdate() {
    if (this.props.items.length > 0) {
      this.setState(() => {
        return {
          open: true,
          items: this.props.items,
        };
      });
    }
  }

  componentDidMount() {
    if (this.props.items.length > 0) {
      console.log(this.props.items);
      this.setState(() => {
        return {
          open: true,
          items: this.props.items,
        };
      });
    }
    this.setState(() => {
      return {};
    });
  }

  render() {
    var arrays = [];
    const { translate } = this.props;
    var settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
    };

    const { symbol } = this.props;
    var items = this.props.items;
    console.log(items);
    if (this.state.open) {
      if (this.state.items) {
        items = this.props.items;
        console.log(items);
      }
      while (items.length > 0) {
        arrays.push(items.splice(0, 3));
        console.log(arrays);
      }
      return (
        <div className="theme-card">
          <h5 className="title-border"> {translate("new_product")}</h5>
          <Slider className="offer-slider slide-1" {...settings}>
            {arrays.map((products, index) => {
              return (
                <div data-index={index} key={index}>
                  {products.map((product, i) => {
                    return (
                      <div data-index={index} className="media" key={i}>
                        <Link
                          to={`${process.env.PUBLIC_URL}/left-sidebar/product/${
                            product.id
                          }`}
                        >
                          <img
                            className="img-fluid"
                            src={`${product.pictures[0]}`}
                            alt=""
                          />
                        </Link>
                      </div>
                    );
                  })}
                </div>
              );
            })}
          </Slider>
        </div>
      );
    } else {
      return <h6>{translate("loading")}</h6>;
    }
  }
}

function mapStateToProps(state) {
  return {
    items: state.data.products,
    symbol: state.data.symbol,
  };
}

export default connect(mapStateToProps)(withTranslate(NewProduct));
