import React, { Component } from "react";
import { Link } from "react-router-dom";

const tree1 = "Products";
const tree2 = ["Drinks", "Meal", "FruitsVegetables", "Meat", "Fish", "Bread"];
var tree = [];

class Breadcrumb extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: null,
      parent: null,
      data: null,
    };
  }

  componentWillMount() {
    if (this.props) {
      const { title, parent, data } = this.props;
      if (data !== this.state.data) {
        this.setState(() => {
          return {
            title,
            parent,
            data,
          };
        });
        if (data) {
          console.log(data);

          this.build(title, data);
        }
      }
    }
  }

  componentDidUpdate() {
    if (this.props) {
      const { title, parent, data } = this.props;
      if (data !== this.state.data) {
        this.setState(() => {
          return {
            title,
            parent,
            data,
          };
        });
        if (data) {
          console.log(data);
          this.build(title, data);
        }
      }
    }
  }

  build = (title, data) => {
    const categoryList = this.props.data.state.data2.categoryTree;

    var itemCategories = [];
    var tree = [];
    tree.push(tree1);
    var itemId = null;
    categoryList.map((c) => {
      if (c.name === title) {
        itemId = c.id;
        console.log(itemId);
        this.hasParent(itemId, categoryList);
      }
    });
    console.log(tree);
  };

  hasParent(id, categoryList) {
    categoryList.map((c) => {
      if (c.id === id) {
        if (c.parentId !== 0) {
          console.log(c.name);
          tree.push(c.name);
          this.hasParent(c.parentId, categoryList);
        }
      }
    });
  }

  newTo = (key) => {
    return {
      pathname: `${process.env.PUBLIC_URL}/no-sidebar/collection/`,
      category: key,
    };
  };

  itemLink = (product) => {
    return (
      <Link to={`${process.env.PUBLIC_URL}/left-sidebar/product/${product.id}`}>
        <h2>
          {product} {" / "}{" "}
        </h2>
      </Link>
    );
  };

  collectionLink = (name) => {
    return (
      <Link to={this.newTo(name)}>
        <h2>
          {name} {" / "}
        </h2>
      </Link>
    );
  };

  Title = (tree) => {
    return <div />;
  };

  render() {
    const { title, parent, data } = this.props;
    console.log(this.props);
    const tree2 = [
      "Drinks",
      "Meal",
      "FruitsVegetables",
      "Meat",
      "Fish",
      "Bread",
    ];

    // {tree1} + " / " + {tree2} + " / " + {treeN} + " / " + {treeN+1}

    //I have the category  of the item
    var treeN,
      treeN1,
      treeN2 = null;

    return (
      <div className="breadcrumb-section">
        <div className="container">
          <div className="row">
            <div className="col-md-6">
              <div className="page-title" />
            </div>
            <div className="col-md-6">
              <nav aria-label="breadcrumb" className="theme-breadcrumb">
                <ol className="breadcrumb">
                  <li className="breadcrumb-item">
                    <Link to={`${process.env.PUBLIC_URL}`}>Home</Link>
                  </li>
                  {parent ? (
                    <li className="breadcrumb-item" aria-current="page">
                      {parent}
                    </li>
                  ) : (
                    ""
                  )}
                  <li className="breadcrumb-item active" aria-current="page">
                    {title}
                  </li>
                </ol>
              </nav>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Breadcrumb;
