import shop from "../api/shop";
import * as types from "../constants/ActionTypes";
import store from "../store";
import {
    toast
} from "react-toastify";
import "react-toastify/dist/ReactToastify.min.css";

const wooConfig = require("../api/wooConfig");
const WooCommerceAPI = require("woocommerce-api");
const WooCommerce = new WooCommerceAPI({
    url: wooConfig.siteUrl,
    consumerKey: wooConfig.consumerKey,
    consumerSecret: wooConfig.consumerSecret,
    wpAPI: true,
    version: "wc/v3",
});

export const fetchProductsBegin = () => ({
    type: types.FETCH_PRODUCTS_BEGIN,
});

export const receiveProducts = (products) => ({
    type: types.RECEIVE_PRODUCTS,
    products,
});

export const getAllProducts = (W) => (dispatch) => {
    dispatch(fetchProductsBegin());
    WooCommerce.getAsync(`products?per_page=30`)
        .then((res) => res.toJSON().body)
        .then((json) => {
            var data = json;
            Object.json1 = JSON.parse(data);
            var items = Object.json1;
            var products = [];
            console.log(items);
            items.map(
                ({
                    id,
                    name,
                    images,
                    price,
                    categories,
                    description,
                    stock_quantity,
                    on_sale,
                    sale_price,
                    short_description,
                }) => {
                    var img;
                    images.map((data) => {
                        img = data.src;
                    });

                    const newItemO = {
                        id: id,
                        title: name,
                        img: img,
                        price: parseInt(price),
                        company: "nurumia",
                        info: description,
                        inCart: null,
                        count: 1,
                        total: 1,
                        categories: categories,
                        stock: stock_quantity,
                    };

                    const newItem = {
                        category: "vegetable",
                        description: description,
                        discount: 10,
                        id: id,
                        name: name,
                        new: false,
                        pictures: images,
                        length: images.length,
                        price: price,
                        sale: on_sale,
                        salePrice: sale_price,
                        shortDetails: short_description,
                        stock: stock_quantity,
                    };

                    products.push(newItem); // Push the object
                    this.oldProducts = true;
                }
            );
            console.log(products);
            dispatch(receiveProducts(products));
            return products;
        });
    // shop.getProducts((products) => {
    //     dispatch(receiveProducts(products));
    //     return products;
    // });
};

export const fetchSingleProduct = (productId) => ({
    type: types.FETCH_SINGLE_PRODUCT,
    productId,
});

//it seems that I should probably use this as the basis for "Cart"
export const addToCart = (product, qty) => (dispatch) => {
    toast.success("Item Added to Cart");
    dispatch(addToCartUnsafe(product, qty));
};
export const addToCartAndRemoveWishlist = (product, qty) => (dispatch) => {
    toast.success("Item Added to Cart");
    dispatch(addToCartUnsafe(product, qty));
    dispatch(removeFromWishlist(product));
};
export const addToCartUnsafe = (product, qty) => ({
    type: types.ADD_TO_CART,
    product,
    qty,
});
export const removeFromCart = (product_id) => (dispatch) => {
    toast.error("Item Removed from Cart");
    dispatch({
        type: types.REMOVE_FROM_CART,
        product_id,
    });
};
export const incrementQty = (product, qty) => (dispatch) => {
    toast.success("Item Added to Cart");
    dispatch(addToCartUnsafe(product, qty));
};
export const decrementQty = (productId) => (dispatch) => {
    toast.warn("Item Decrement Qty to Cart");

    dispatch({
        type: types.DECREMENT_QTY,
        productId,
    });
};

//it seems that I should probably use this as the basis for "Wishlist"
export const addToWishlist = (product) => (dispatch) => {
    toast.success("Item Added to Wishlist");
    dispatch(addToWishlistUnsafe(product));
};
export const addToWishlistUnsafe = (product) => ({
    type: types.ADD_TO_WISHLIST,
    product,
});
export const removeFromWishlist = (product_id) => (dispatch) => {
    toast.error("Item Removed from Wishlist");
    dispatch({
        type: types.REMOVE_FROM_WISHLIST,
        product_id,
    });
};

//Compare Products
export const addToCompare = (product) => (dispatch) => {
    toast.success("Item Added to Compare");
    dispatch(addToCompareUnsafe(product));
};
export const addToCompareUnsafe = (product) => ({
    type: types.ADD_TO_COMPARE,
    product,
});
export const removeFromCompare = (product_id) => ({
    type: types.REMOVE_FROM_COMPARE,
    product_id,
});

// Filters
export const filterBrand = (brand) => ({
    type: types.FILTER_BRAND,
    brand,
});
export const filterColor = (color) => ({
    type: types.FILTER_COLOR,
    color,
});
export const filterPrice = (value) => ({
    type: types.FILTER_PRICE,
    value,
});
export const filterSort = (sort_by) => ({
    type: types.SORT_BY,
    sort_by,
});

// Currency
export const changeCurrency = (symbol) => ({
    type: types.CHANGE_CURRENCY,
    symbol,
});
