import React, { Component } from "react";
import { storeProducts, detailProduct } from "../data";
import { tree } from "../categoryContext";
import WooContext from "./wooContext";

const wooConfig = require("../components/wooConfig");
const WooCommerceAPI = require("woocommerce-api");

const WooCommerce = new WooCommerceAPI({
  url: wooConfig.siteUrl,
  consumerKey: wooConfig.consumerKey,
  consumerSecret: wooConfig.consumerSecret,
  wpAPI: true,
  version: "wc/v3",
});

const ProductContext = React.createContext();

var pos = 1;

var addresses = [
  {
    id: 19,
    level: 0,
    position: 0,
    name: "Product Categories",
  },
];
var cart = [];
var length = null;

class ProductProvider extends Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      sales: [],
      tree: [],
      tree2: [],
      imageClicked: null,
      categoryClicked: null,
      detailProduct: detailProduct,
      cart: [],
      // preCart: [],
      modalOpen: false,
      modalProduct: detailProduct,
      cartSubTotal: 0,
      cartTax: 0,
      cartTotal: 0,
      isListLoaded: false,
      isTreeLoaded: false,
      isMenuLoaded: false,
    };
    this.imageClick = this.imageClick.bind(this);
    this.categoryClick = this.categoryClick.bind(this);
  }
  static contextType = WooContext;

  componentWillMount() {
    // this.createTree();
    // this.getSales();
  }

  componentDidMount() {
    //  this.getList();
  }

  getList = (res) => {
    WooCommerce.getAsync(`products?category=${res}`)
      .then((res) => res.toJSON().body)
      .then((json) => {
        var data = json;
        Object.json1 = JSON.parse(data);
        var items = Object.json1;
        var products = [];
        items.map(
          ({
            id,
            name,
            images,
            price,
            categories,
            description,
            stock_quantity,
          }) => {
            var img;
            images.map((data) => {
              img = data.src;
            });

            const newItem = {
              id: id,
              title: name,
              img: img,
              price: parseInt(price),
              company: "nurumia",
              info: description,
              inCart: null,
              count: 1,
              total: 1,
              categories: categories,
              stock: stock_quantity,
            };

            products.push(newItem); // Push the object
            this.oldProducts = true;
          }
        );
        this.setState({
          ...{
            isListLoaded: true,
            products: products,
          },
        });
      });
  };

  getSales2 = () => {
    WooCommerce.getAsync(`products?on_sale=true`)
      .then((res) => res.toJSON().body)
      .then((json) => {
        var data = json;
        Object.json1 = JSON.parse(data);
        var items = Object.json1;
        var sales = [];
        items.map(
          ({ id, name, images, sale_price, categories, description }) => {
            var img;

            images.map((data) => {
              img = data.src;
            });
            const newItem = {
              id: id,
              title: name,
              img: img,
              price: parseInt(sale_price),
              company: "nurumia",
              info: description,
              inCart: false,
              count: 1,
              total: 1,
              categories: categories,
            };
            sales.push(newItem); // Push the object
          }
        );

        this.setState({
          ...{
            sales: sales,
          },
        });
      });
  };

  getCategories = (parent, level) => {
    return WooCommerce.getAsync(
      `products/categories?hide_empty=false&per_page=30${parent}`
    ).then((res) => {
      if (res) {
        var data = res.toJSON().body;
        // console.log(data)
        Object.json1 = JSON.parse(data);
        var items = Object.json1;
        var categories = [];

        if (items[0]) {
          if (items[0].id != undefined) {
            pos = 0;

            items.map(({ id, name, parent }) => {
              const newCategory = {
                id: id,
                name: name,
                parent: parent,
                children: [],
              };
              const addr = {
                id: id,
                level: level,
                position: pos,
                name: name,
              };

              addresses.push(addr);
              categories.push(newCategory); // Push the object

              pos++;
            });

            return categories;
          }
        }
      }
    });
  };

  render() {
    if (this.state.isTreeLoaded !== true) {
      return (
        <div>
          <ProductContext.Provider
            value={{
              ...this.state,
            }}
          >
            {" "}
            {this.props.children}{" "}
          </ProductContext.Provider>{" "}
        </div>
      );
    }
  }
}

const ProductConsumer = ProductContext.Consumer;

export { ProductProvider, ProductConsumer };

export function useProduct() {
  return useContext(ProductContext);
}

export default ProductContext;
