import {
    FETCH_CATEGORYTREE,
    RECEIVE_CATEGORYTREE
} from "../constants/ActionTypes";


const initialState = {
    categoryTree: [],
    category: null
};

const categoryTreeReducer = (state = initialState, action) => {
    switch (action.type) {
        case RECEIVE_CATEGORYTREE:
            return {
                ...state,
                categoryTree: action.categoryTree
            };
        case FETCH_CATEGORYTREE:
            if (state.categoryTree.findIndex(category => {
                    if (category.id !== -1) {
                        const singleCategory = state.categoryTree.reduce((itemAcc, categoryTree) => {
                            return categoryTree
                        }, [])
                        return {
                            ...state,
                            category: singleCategory
                        };
                    }
                }));
        default:
            return state;
    }
};
export
default categoryTreeReducer;
