import React, { Component } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.scss';
import { connect } from "react-redux";
import SimpleReactValidator from "simple-react-validator";
import { postReview, getReviews } from "../../../actions/indexO";

import { Link } from 'react-router-dom'

class DetailsTopTabs extends Component {
    constructor() {
        super();
        this.state = {
            stars: ["on", "on", "on", "on", "on"], id: "", name: "", title: "", review: "", email: ""
        };
    }
    componentWillMount() {
        console.log(this.props)
        if (this.props.item.id) {
            this.setState(() => {
                return { id: this.props.item.id }
            })
        }

        this.props.getReviews(this.props.item.id)
    }

    fetchRating = (n) => {
        var stars = [];

        for (var i = 0; i <= 4; i++) {
            if (n >= i) {
                stars.push("on")
            } else {
                stars.push("off")
            }
            this.setState(() => { return { stars } })
        }
    }

    handleChange = (e) => {
        var value = e.currentTarget.value;
        const name = e.currentTarget.name;
        if (e.currentTarget.name) {
            this.setState(() => {
                return { [name]: value };
            });
        } else return null;
    };

    handleRateClick = (e) => {
        console.log(e.currentTarget)
        this.fetchRating(e.currentTarget.id)
    }


    handleSubmit(event) {
        console.log(this.state)

        event.preventDefault();

        const {
            id, name, title, review, email, stars
        } = this.state;

        const data = {
            product_id: id,
            review: review,
            reviewer: name,
            reviewer_email: email,
            rating: stars.length()
        };
        this.props.postReview(data);

    }

    render() {

        return (
            <section className="tab-product m-0">
                <div className="row">
                    <div className="col-sm-12 col-lg-12">
                        <Tabs className="tab-content nav-material">
                            <TabList className="nav nav-tabs nav-material">

                                <Tab className="nav-item">
                                    <span className="nav-link " ><i className="icofont icofont-man-in-glasses"></i>Details</span>
                                    <div className="material-border"></div>
                                </Tab>
                                <Tab className="nav-item">
                                    <span className="nav-link" >
                                        <i className="icofont icofont-contacts"></i>Video</span>
                                    <div className="material-border"></div>
                                </Tab>
                                <Tab className="nav-item">
                                    <span className="nav-link active" >
                                        <i className="icofont icofont-contacts"></i>Write Review</span>
                                    <div className="material-border"></div>
                                </Tab>
                            </TabList>
                            <TabPanel>
                                {this.props.description ? this.props.description : <p className="mt-4 p-0">
                                    Lorem Ipsum is simply dummy text of the printing and
                                    typesetting industry. Lorem Ipsum has been the industry's
                                    standard dummy text ever since the 1500s, when an unknown
                                    printer took a galley of type and scrambled it to make a
                                    type specimen book. It has survived not only five centuries,
                                    but also the leap into electronic typesetting, remaining
                                    essentially unchanged. It was popularised in the 1960s with
                                    the release of Letraset sheets containing Lorem Ipsum
                                    passages, and more recently with desktop publishing software
                                    like Aldus PageMaker including versions of Lorem Ipsum.
                                </p>}
                            </TabPanel>
                            <TabPanel>
                                <div className="mt-4 text-center">
                                    <div className="embed-responsive embed-responsive-16by9">
                                        <iframe
                                            src="https://www.youtube.com/embed/BUWzX78Ye_8"
                                            allow="autoplay; encrypted-media"
                                            allowFullScreen>
                                        </iframe>
                                    </div>
                                </div>
                            </TabPanel>
                            <TabPanel>
                                <form className="theme-form mt-4" >
                                    <div className="form-row">
                                        <div className="col-md-12 ">
                                            <div className="media m-0">
                                                <label>Rating</label>
                                                <div className="media-body ml-3">
                                                    {this.state.stars.map((s, i) => <i key={i} id={i} className={`fa fa-star my-rating  ${s}`} onClick={this.handleRateClick} ></i>)}

                                                </div>
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <label htmlFor="name">Name</label>
                                            <input type="text" className="form-control" id="name" placeholder="Enter Your name" name="name" value={this.state.name} onChange={this.handleChange} required />
                                        </div>
                                        <div className="col-md-6">
                                            <label htmlFor="email">Email</label>
                                            <input type="text" className="form-control" id="email" placeholder="Enter Your Email" name="email" value={this.state.email} onChange={this.handleChange} required />
                                        </div>
                                        <div className="col-md-12">
                                            <label htmlFor="review">Review Title</label>
                                            <input type="text" className="form-control" id="title" placeholder="Enter your Review Subjects" name="title" value={this.state.title} onChange={this.handleChange} required />
                                        </div>
                                        <div className="col-md-12">
                                            <label htmlFor="review">Review </label>
                                            <textarea className="form-control" placeholder="Write Your Testimonial Here" id="review" name="review" value={this.state.review} onChange={this.handleChange} required rows="6"  ></textarea>
                                        </div>
                                        <div className="col-md-12">
                                            <button className="btn btn-solid" >Submit Your Review</button>
                                        </div>
                                    </div>
                                </form>
                            </TabPanel>
                        </Tabs>
                    </div>
                </div>
            </section>
        )
    }
}


const mapStateToProps = (state) => ({
    state,
});

export default connect(mapStateToProps, {
    postReview, getReviews
})(DetailsTopTabs);
