import React, { Component } from "react";
import { Link } from "react-router-dom";

const tree1 = "Products";
const tree2 = ["Drinks", "Meal", "FruitsVegetables", "Meat", "Fish", "Bread"];

class Breadcrumb extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: null,
      parent: null,
      data: null,
    };
    var tree = [];
  }

  componentWillMount() {
    if (this.props) {
      const { title, parent, data } = this.props;
      if (data !== this.state.data) {
        this.setState(() => {
          return {
            title,
            parent,
            data,
          };
        });
        if (data) {
          this.build(data);
        }
      }
    }
  }

  build = (data) => {
    const categoryList = this.props.data.state.data2.categoryTree;
    const products = this.props.data.state.data.products;

    var itemCategories = [];
    var tree = [];
    tree.push(tree1);
    this.hasParent(data.title, categoryList);
    console.log(tree);
  };

  hasParent(name, categoryList) {
    var parent = categoryList.map((c) => {
      if (c.name === name) {
        var data = { name: c.name, parentId: c.parentId };
        tree.push(name);
        this.hasParent(name);
      }
    });
  }

  newTo = (key) => {
    return {
      pathname: `${process.env.PUBLIC_URL}/no-sidebar/collection/`,
      category: key,
    };
  };

  itemLink = (product) => {
    return (
      <Link to={`${process.env.PUBLIC_URL}/left-sidebar/product/${product.id}`}>
        <h2>
          {product} {" / "}{" "}
        </h2>
      </Link>
    );
  };

  collectionLink = (name) => {
    return (
      <Link to={this.newTo(name)}>
        <h2>
          {name} {" / "}
        </h2>
      </Link>
    );
  };

  Title = (tree) => {
    return (
      <div>
        {tree.map((name) => {
          if (
            !products.map((p) => {
              if (p.name === name) {
                return itemLink(name);
              }
            })
          ) {
            return collectionLink(name);
          }
        })}
      </div>
    );
  };

  render() {
    const { title, parent, data } = this.props;
    console.log(this.props);
    const tree2 = [
      "Drinks",
      "Meal",
      "FruitsVegetables",
      "Meat",
      "Fish",
      "Bread",
    ];

    // {tree1} + " / " + {tree2} + " / " + {treeN} + " / " + {treeN+1}

    //I have the category  of the item
    var treeN,
      treeN1,
      treeN2 = null;

    return (
      <div className="breadcrumb-section">
        <div className="container">
          <div className="row">
            <div className="col-md-6">
              <div className="page-title" />
            </div>
            <div className="col-md-6">
              <nav aria-label="breadcrumb" className="theme-breadcrumb">
                <ol className="breadcrumb">
                  <li className="breadcrumb-item">
                    <Link to={`${process.env.PUBLIC_URL}`}>Home</Link>
                  </li>
                  {parent ? (
                    <li className="breadcrumb-item" aria-current="page">
                      {parent}
                    </li>
                  ) : (
                    ""
                  )}
                  <li className="breadcrumb-item active" aria-current="page">
                    {title}
                  </li>
                </ol>
              </nav>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Breadcrumb;
