    const tree2 = [
      "Drinks",
      "Meal",
      "FruitsVegetables",
      "Meat",
      "Fish",
      "Bread",
    ];

    // {tree1} + " / " + {tree2} + " / " + {treeN} + " / " + {treeN+1}

    //I have the category  of the item
    var treeN,
      treeN1,
      treeN2 = null;

    if (data) {
      const categoryList = data.state.data2.categoryTree;

      const checkTree2 = (name) => {
        tree2.map((n) => {
          if (n === name) {
            return "Done!";
          } else null;
        });
      };

      const findParent = (itemId) => {
        var parent = null;
        categoryList.map((c) => {
          if (c.id === itemId) {
            parent = { name: c.name, parentId: c.parent };
          }
        });
        return parent;
      };
      const treeN = title; //Mollejas    / Onion
      categoryList.map((c) => {
        if (c.name === title) {
          const treeParentId = c.parent; //finds the parentId of the product
          categoryList.map((c1) => {
            if (c1.id === treeParentId) {
              treeN = c1.name; // this is the parent name
