import {RECEIVE_ORDER_RECEIPT, PLACE_ORDER} from "../constants/ActionTypes";

const initialState = {
  orderData: [],
};

const orderReducer = (state = initialState, action) => {
  console.log(state);
  console.log(action);
  switch (action.type) {
    case PLACE_ORDER:
      return {
        ...state,
      };
    case RECEIVE_ORDER_RECEIPT:
      return {};
    default:
      return state;
  }
};

export default orderReducer;
