import React, { Component } from "react";
import { useContext } from "react";
import _products from "./data.json";

const wooConfig = require("./wooConfig");
const WooCommerceAPI = require("woocommerce-api");
/* category: "vegetable"
description: "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industrys standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum. Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industrys standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum."
discount: 10
id: 49
name: "Cabbage"
new: false
pictures: Array(4)
0: "/assets/images/vegetables/pro/2.jpg"
1: "/assets/images/vegetables/pro/4.jpg"
2: "/assets/images/vegetables/pro/1.jpg"
3: "/assets/images/vegetables/pro/4.jpg"
length: 4
__proto__: Array(0)
price: 120
sale: false
salePrice: 240
shortDetails: "Sed ut perspiciatis, unde omnis iste natus error sit voluptatem accusantium doloremque laudantium, totam rem aperiam eaque ipsa, quae ab illo inventore veritatis et quasi architecto beatae vitae dicta sunt, explicabo. Nemo enim ipsam voluptatem,"
stock: 2 */

const TIMEOUT = 100;

export default {
  //getProducts: (cb, timeout) =>
  // setTimeout(() => cb(_products), timeout || TIMEOUT),

  getData(res) {
    console.log("get products");
    WooCommerce.getAsync(`products?per_page=30`)
      .then((res) => res.toJSON().body)
      .then((json) => {
        var data = json;
        Object.json1 = JSON.parse(data);
        var items = Object.json1;
        var products = [];
        console.log(items);
        items.map(
          ({
            id,
            name,
            images,
            price,
            categories,
            description,
            stock_quantity,
            on_sale,
            sale_price,
            short_description,
          }) => {
            var img;
            images.map((data) => {
              img = data.src;
            });

            const newItemO = {
              id: id,
              title: name,
              img: img,
              price: parseInt(price),
              company: "nurumia",
              info: description,
              inCart: null,
              count: 1,
              total: 1,
              categories: categories,
              stock: stock_quantity,
            };

            const newItem = {
              category: "vegetable",
              description: description,
              discount: 10,
              id: id,
              name: name,
              new: false,
              pictures: images,
              length: images.length,
              price: price,
              sale: on_sale,
              salePrice: sale_price,
              shortDetails: short_description,
              stock: stock_quantity,
            };

            products.push(newItem); // Push the object
            this.oldProducts = true;
          }
        );
        console.log(products);
        return products;
      });
  },
  buyProducts: (payload, cb, timeout) =>
    setTimeout(() => cb(), timeout || TIMEOUT),
};

const WooCommerce = new WooCommerceAPI({
  url: wooConfig.siteUrl,
  consumerKey: wooConfig.consumerKey,
  consumerSecret: wooConfig.consumerSecret,
  wpAPI: true,
  version: "wc/v3",
});

const ProductContext = React.createContext();

var pos = 1;

var addresses = [
  {
    id: 19,
    level: 0,
    position: 0,
    name: "Product Categories",
  },
];
var cart = [];
var length = null;

class ProductProvider extends Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      sales: [],
      tree: [],
      tree2: [],
      imageClicked: null,
      categoryClicked: null,
      detailProduct: null,
      cart: [],
      // preCart: [],
      modalOpen: false,
      modalProduct: null,
      cartSubTotal: 0,
      cartTax: 0,
      cartTotal: 0,
      isListLoaded: false,
      isTreeLoaded: false,
      isMenuLoaded: false,
    };
    this.imageClick = this.imageClick.bind(this);
    this.categoryClick = this.categoryClick.bind(this);
  }

  componentWillMount() {
    console.log("Will mount");
    // this.createTree();
    // this.getSales();
  }

  componentDidMount() {
    //  this.getList();
  }

  getList = (res) => {
    WooCommerce.getAsync(`products?category=${res}`)
      .then((res) => res.toJSON().body)
      .then((json) => {
        var data = json;
        Object.json1 = JSON.parse(data);
        var items = Object.json1;
        var products = [];
        items.map(
          ({
            id,
            name,
            images,
            price,
            categories,
            description,
            stock_quantity,
          }) => {
            var img;
            images.map((data) => {
              img = data.src;
            });

            const newItem = {
              id: id,
              title: name,
              img: img,
              price: parseInt(price),
              company: "nurumia",
              info: description,
              inCart: null,
              count: 1,
              total: 1,
              categories: categories,
              stock: stock_quantity,
            };

            products.push(newItem); // Push the object
            this.oldProducts = true;
          }
        );
        this.setState({
          ...{
            isListLoaded: true,
            products: products,
          },
        });
      });
  };

  getSales2 = () => {
    WooCommerce.getAsync(`products?on_sale=true`)
      .then((res) => res.toJSON().body)
      .then((json) => {
        var data = json;
        Object.json1 = JSON.parse(data);
        var items = Object.json1;
        var sales = [];
        items.map(
          ({ id, name, images, sale_price, categories, description }) => {
            var img;

            images.map((data) => {
              img = data.src;
            });
            const newItem = {
              id: id,
              title: name,
              img: img,
              price: parseInt(sale_price),
              company: "nurumia",
              info: description,
              inCart: false,
              count: 1,
              total: 1,
              categories: categories,
            };
            sales.push(newItem); // Push the object
          }
        );

        this.setState({
          ...{
            sales: sales,
          },
        });
      });
  };

  getCategories = (parent, level) => {
    return WooCommerce.getAsync(
      `products/categories?hide_empty=false&per_page=30${parent}`
    ).then((res) => {
      if (res) {
        var data = res.toJSON().body;
        // console.log(data)
        Object.json1 = JSON.parse(data);
        var items = Object.json1;
        var categories = [];

        if (items[0]) {
          if (items[0].id != undefined) {
            pos = 0;

            items.map(({ id, name, parent }) => {
              const newCategory = {
                id: id,
                name: name,
                parent: parent,
                children: [],
              };
              const addr = {
                id: id,
                level: level,
                position: pos,
                name: name,
              };

              addresses.push(addr);
              categories.push(newCategory); // Push the object

              pos++;
            });

            return categories;
          }
        }
      }
    });
  };

  render() {
    if (this.state.isTreeLoaded !== true) {
      return (
        <div>
          <ProductContext.Provider
            value={{
              ...this.state,
            }}
          >
            {" "}
            {this.props.children}{" "}
          </ProductContext.Provider>{" "}
        </div>
      );
    }
  }
}

const ProductConsumer = ProductContext.Consumer;

export { ProductProvider, ProductConsumer };

export function useProduct() {
  return useContext(ProductContext);
}
