import {
  FETCH_LOGIN_BEGIN,
  RECEIVE_LOGIN,
  FETCH_LOGIN,
} from "../constants/ActionTypes";

const initialState = {
  user: null,
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case RECEIVE_LOGIN:
      return {
        ...state,
        user: action.user,
      };
    case FETCH_LOGIN:
      return {
        ...state,
        product_details: singleItem,
      };

    default:
      return state;
  }
};
export default userReducer;
