import React, {Component} from "react";
import * as emailjs from "emailjs-com";
//AIzaSyADbvaiUnaCa2HuKsVLMYbMUY4DvOUFIjk   GoogleAPImaps
import Breadcrumb from "../breadcrumb";
class Contact extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fname: "name",
      email: "e-mail",
      subject: "subject",
      message: "message",
      mailSent: false,
      error: null,
    };
    this.handleFormSubmit = this.handleFormSubmit.bind(this);
  }

  handleFormSubmit(e) {
    e.preventDefault();
    const {fname, email, subject, message} = this.state;
    let templateParams = {
      from_name: fname,
      user_mail: email,
      subject: subject,
      message: message,
    };
    emailjs
      .send(
        "default_service",
        "emailtemplate",
        templateParams,
        "user_FqIXKAIpDQYC1lkU3it2G"
      )
      .then(
        (res) => {
          console.log(res);
        },
        (err) => {
          console.log("FAILED...", err);
        }
      );
    this.resetForm();
  }

  resetForm() {
    this.setState({
      fname: "",
      email: "",
      subject: "",
      message: "",
      mailSent: true,
      error: null,
    });
  }
  /*   src="https://www.google.com/maps/embed?pb=!1m14!1m12!1m3!1d50059.12775918716!2d72.78534673554945!3d21.16564923510817!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!5e0!3m2!1sen!2sin!4v1533793756956"
   */
  render() {
    return (
      <div>
        <Breadcrumb title={"Contactanos"} />

        {/*Forget Password section*/}
        <section className=" contact-page section-b-space">
          <div className="container">
            <div className="row section-b-space">
              <div className="col-lg-7 map">
                <iframe
                  src="https://www.google.com/maps/embed/v1/place/Calle+Armando+Palacio+Valdés,+10,+33600+Mieres,+Asturias/@43.2513279,-5.7765058,19z/data=!4m13!1m7!3m6!1s0xd36f6889d124981:0x6cd613e0c84b5ec2!2sCalle+Armando+Palacio+Valdés,+10,+33600+Mieres,+Asturias!3b1!8m2!3d43.2513417!4d-5.7755578!3m4!1s0xd36f6889d124981:0x6cd613e0c84b5ec2!8m2!3d43.2513417!4d-5.7755578"
                  allowFullScreen
                ></iframe>
              </div>
              <div className="col-lg-5">
                <div className="contact-right">
                  <ul>
                    <li>
                      <div className="contact-icon">
                        <img
                          src={`${process.env.PUBLIC_URL}/assets/images/icon/phone.png`}
                          alt="Generic placeholder image"
                        />
                        <h6>Contactanos</h6>
                      </div>
                      <div className="media-body">
                        <p>+91 123 - 456 - 7890</p>
                        <p>+86 163 - 451 - 7894</p>
                      </div>
                    </li>
                    <li>
                      <div className="contact-icon">
                        <i className="fa fa-map-marker" aria-hidden="true"></i>
                        <h6>Direccion</h6>
                      </div>
                      <div className="media-body">
                        <p>C/ Armando Palacio Valdés , 10 </p>
                        <p> Mieres 33600</p>
                      </div>
                    </li>
                    <li>
                      <div className="contact-icon">
                        <img
                          src={`${process.env.PUBLIC_URL}/assets/images/icon/email.png`}
                          alt="Generic placeholder image"
                        />
                        <h6>Direccion electronica</h6>
                      </div>
                      <div className="media-body">
                        <p>Support@delprado.com</p>
                        <p>info@delprado.com</p>
                      </div>
                    </li>
                  </ul>
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col-sm-12">
                <form className="theme-form">
                  <div className="form-row">
                    <div className="col-md-6">
                      <label htmlFor="name">Nombre</label>
                      <input
                        className="form-control"
                        type="text"
                        size={35}
                        id="contactName"
                        name="contactName"
                        value={this.state.fname ? this.state.fname : "Name"}
                        onChange={(e) => this.setState({fname: e.target.value})}
                      />
                    </div>

                    <div className="col-md-6">
                      <label htmlFor="email">Email</label>
                      <input
                        type="text"
                        size={35}
                        id="contactEmail"
                        name="contactEmail"
                        value={this.state.mail ? this.state.mail : "e-mail"}
                        onChange={(e) => this.setState({mail: e.target.value})}
                      />
                    </div>
                    <div>
                      <label htmlFor="contactSubject">Asunto</label>
                      <input
                        type="text"
                        size={35}
                        id="contactSubject"
                        name="contactSubject"
                        value={
                          this.state.subject ? this.state.subject : "Subject"
                        }
                        onChange={(e) =>
                          this.setState({subject: e.target.value})
                        }
                      />
                    </div>
                    <div className="col-md-12">
                      <label htmlFor="review">Escribe aqui tu mensage</label>
                      <textarea
                        className="form-control"
                        cols={50}
                        rows={6}
                        id="contactMessage"
                        name="contactMessage"
                        value={
                          this.state.message ? this.state.message : "Message"
                        }
                        onChange={(e) =>
                          this.setState({message: e.target.value})
                        }
                      ></textarea>
                    </div>
                    <div className="col-md-12">
                      <button
                        className="btn btn-solid"
                        onClick={this.handleFormSubmit}
                      >
                        Enviar
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </section>
      </div>
    );
  }
}

export default Contact;
