import React, {useState} from "react";
import {createContext, useContext} from "react";
import axios from "axios";

const wooConfig = require("./wooConfig");
const WooCommerceAPI = require("woocommerce-api");

const WooCommerce = new WooCommerceAPI({
  url: wooConfig.siteUrl,
  consumerKey: wooConfig.consumerKey,
  consumerSecret: wooConfig.consumerSecret,
  wpAPI: true,
  version: "wc/v3",
});

const targetUrl = "https://grassrootemarket.com/wp-json/";
const urlRegister = "wp/v2/users/register";
const urlJWT = "jwt-auth/v1/token";
const userURI = targetUrl + "wp/v2/users/me";

/**
 * Signup Flow
 *  1-Create user : Creates an authenticated Wordpress Account with password
 *  2-Create user in woocommerce -> createCustomer(newcustomer)
 *  3-receiveLogin(data{user,email}) -> fillCustomer(mail)
 *  4-getCustomerId(newCustomer) -> updateCustomer(userId, newCustomer)
 */

const login = async (user) => {
  var userName = user.name; //this is the nice_name
  var password = user.password;
  var token,
    displayName,
    niceName,
    email = null;
  console.log(user);
  return await axios
    .post("https://grassrootemarket.com/wp-json/jwt-auth/v1/token", {
      username: userName,
      password: password,
    })
    .then((result) => {
      if (result.status === 200) {
        token = result.data.token;
        displayName = result.data.user_display_name;
        niceName = result.data.user_nicename;
        email = result.data.user_email;
        var filledFields = null;
        return fillCustomer(email).then((res) => {
          if (res) {
            filledFields = {
              ...res,
              name: displayName,
              email: email,
              token: token,
            };
          }
          return filledFields;
        });
      } else {
        alert("error");
      }
    })
    .catch((e) => {
      alert("error");
    });
};

//var data = [user, shipping];

const signup = async (userData) => {
  console.log(userData);
  const {
    username,
    email,
    password,
    address_1,
    city,
    country,
    first_name,
    last_name,
    phone,
    postcode,
    state,
  } = userData;

  var user = {
    username: username,
    email: email,
    password: password,
  };

  const newCustomer = {
    email: email,
    first_name: first_name,
    last_name: last_name,
    username: username,
    billing: {
      first_name: first_name,
      last_name: last_name,
      company: "company",
      address_1: address_1,
      address_2: "",
      city: city,
      state: state,
      postcode: postcode,
      country: "Spain",
      email: email,
      phone: phone,
    },
    shipping: {
      first_name: first_name,
      last_name: last_name,
      company: "",
      address: address_1,
      address2: "",
      city: city,
      state: state,
      cp: postcode,
      country: country,
    },
  };
  const body = JSON.stringify({
    username: username,
    email: email,
    password: password,
  });

  // After filling the vlaue fields,the function will proceed to create
  // first the wordpress user.

  return await fetch(targetUrl + urlRegister, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: body,
  })
    .catch((error) => {
      console.log(error.response.body);
    })
    .then(function(res) {
      return res.json();
    })
    .then(function(res) {
      console.log(res);
      if (res.message) {
        alert(res.message);
        console.log(res.code);
        if (res.code === 200) {
          console.log("create cutomer");
          return createCustomer(newCustomer, email).then((res) => {
            console.log(res);
          });

          return cb;
        }
      }

      //  return user.token;
    });
};

const createCustomer = (newCustomer, email) => {
  console.log(newCustomer, email);
  if (!email) {
    const data = {
      user_display_name: newCustomer.username,
      user_email: newCustomer.email,
    };
    receiveLogin(data).then((res) => {
      return res;
    });
  }
  return getCustomerId(newCustomer);
};

const fillCustomer = async (email) => {
  return await WooCommerce.getAsync(`customers?email=${email}`)
    .then((response) => {
      if (response) {
        if (response.body) {
          const res = JSON.parse(response.body);
          const user = res[0];
          var thisUser = {
            userId: user.id,
            company: user.billing.company,
            first_name: user.billing.first_name,
            lastName: user.billing.last_name,
            address: user.billing.address_1,
            address2: user.billing.address_2,
            city: user.billing.city,
            state: user.billing.state,
            cp: user.billing.postcode,
            country: user.billing.country,
            phone: user.billing.phone,
          };
          return thisUser;
        }
      }
    })
    .catch((error) => {
      console.log(error);
    });
};

async function updateAccount(id, updatedUser, updatedShipping) {
  // First will check if user values must be modified : name /mail
  // Check
  console.log(id);
  console.log(updatedUser);
  console.log(updatedShipping);

  if (updatedUser) {
    await axios
      .post(`https://grassrootemarket.com/wp-json/wp/v2/users/${id}`, {
        username: updatedUser.userName,
        mail: updatedUser.email,
      })
      .then((result) => {
        if (result.status === 200) {
          console.log(result);
        } else {
          alert("error");
        }
      })
      .catch((e) => {
        alert(e.response.body.message);
      });
  }

  if (updatedShipping) {
    await WooCommerce.putAsync(`customers/${id}`, updatedShipping)
      .then((response) => {
        const customer = JSON.parse(response.body);
        console.log(customer);
      })
      .catch((error) => {});
  }
}

const updateCustomer = async (id, newCustomer) => {
  console.log(id);
  console.log(newCustomer);
  return await WooCommerce.putAsync(`customers/${id}`, newCustomer)
    .then((response) => {
      const customer = JSON.parse(response.body);
      console.log(customer);
      return customer;
    })
    .catch((error) => {});
};

const getCustomerId = async (newCustomer) => {
  console.log(newCustomer);
  return await WooCommerce.getAsync(`customers?email=${newCustomer.email}`)
    .then((response) => {
      if (response) {
        if (response.body) {
          console.log(response.body);
          const user = JSON.parse(response.body);
          const userId = user[0].id;
          console.log(userId);
          return updateCustomer(userId, newCustomer);
        }
      }
    })
    .catch((error) => {
      return null;
    });
};

const createOrder = async (ready, userOrder) => {
  this.fillOrder();
  if (ready) {
    await WooCommerce.postAsync("orders", userOrder)
      .then((response) => {})
      .catch((error) => {});
  }
};

const receiveLogin = (data) => {
  console.log(data);
  this.setState(() => {
    return {
      ...{
        user: data.user_display_name,
        email: data.user_email,
      },
    };
  });
  this.fillCustomer(data.user_email);
  // Also must to retrieve from wooApi the rest of information
  // about the logged user and populate its state
};

const receiveLogout = (data) => {
  this.setState(() => {
    return {
      ...{
        user: null,
        order: null,
        name: null,
        lastName: null,
        email: null,
        address: null,
        address2: null,
        city: null,
        state: null,
        cp: null,
        country: null,
        phone: null,
        cart: null,
        cartSubTotal: null,
        cartTax: null,
        total: null,
      },
    };
  });
  // Also must to retrieve from wooApi the rest of information
  // about the logged user and populate its state
};

const receiveSignup = (data) => {
  const user = data.user;
  const ship = data.shipping;
  this.setState(() => {
    return {
      ...{
        name: user.user_display_name,
        email: user.user_email,
        phone: ship.phone,
        address: ship.street,
        address2: ship.address2,
        city: ship.city,
        state: ship.province,
        cp: ship.postal,
        country: ship.country,
      },
    };
  });
};

export default {login, signup};
